mapper.select:
SELECT A.TEN_ID AS "tenId" , A.TGT_PJT_CD AS "pjtCd" , A.PJT_NM AS "pjtNm" , A.BIZ_UNIT AS "bizUnit" , A.CUST_NM AS "custNm" , A.CAR_TYPE AS "carType" , A.OEM_NM AS "oemNm" , A.PROD_GRP AS "prodGrp" , A.EC_PJT_CD AS "ecPjtCd" , A.BASE_CURRENCY AS "baseCurrency" , A.STS AS "sts" , CASE A.STS WHEN 'T' THEN '대상선정' WHEN 'P' THEN '계획수립' WHEN 'A' THEN '배부확정' WHEN 'E' THEN '평가중' WHEN 'R' THEN '재배부' WHEN 'C' THEN '완료' ELSE '' END AS "stsNm" , TO_CHAR(A.REG_DTTM, 'YYYY-MM-DD') AS "regDttm" , A.RMK AS "rmk" FROM PCM_TGT_PJT_MSTR A WHERE A.TEN_ID = #{g.tenant}
AND A.TGT_PJT_CD LIKE '%' || TRIM(#{p.pjtCd}) || '%'
AND A.BIZ_UNIT = #{p.bizUnit}
AND A.CUST_NM LIKE '%' || TRIM(#{p.custNm}) || '%'
AND A.CAR_TYPE LIKE '%' || TRIM(#{p.carType}) || '%'
ORDER BY A.REG_DTTM DESC

mapper.insert:
INSERT INTO PCM_TGT_PJT_MSTR (
    TEN_ID
  , TGT_PJT_CD
  , PJT_NM
  , BIZ_UNIT
  , CUST_NM
  , CAR_TYPE
  , OEM_NM
  , PROD_GRP
  , EC_PJT_CD
  , BASE_CURRENCY
  , RMK
  , STS
  , REGR_ID
  , REG_DTTM
  , MODR_ID
  , MOD_DTTM
) VALUES (
    #{g.tenant}
  , #{p.pjtCd}
  , #{p.pjtNm}
  , #{p.bizUnit}
  , #{p.custNm}
  , #{p.carType}
  , #{p.oemNm}
  , #{p.prodGrp}
  , #{p.ecPjtCd}
  , #{p.baseCurrency}
  , #{p.rmk}
  , #{p.sts}
  , #{g.username}
  , #{g.now}
  , #{g.username}
  , #{g.now}
)

mapper.update:
UPDATE  PCM_TGT_PJT_MSTR
   SET  PJT_NM    = #{p.pjtNm}
      , BIZ_UNIT  = #{p.bizUnit}
      , CUST_NM   = #{p.custNm}
      , CAR_TYPE  = #{p.carType}
      , OEM_NM    = #{p.oemNm}
      , PROD_GRP  = #{p.prodGrp}
      , EC_PJT_CD = #{p.ecPjtCd}
      , BASE_CURRENCY = #{p.baseCurrency}
      , RMK       = #{p.rmk}
      , MODR_ID   = #{g.username}
      , MOD_DTTM  = #{g.now}
 WHERE  TEN_ID     = #{g.tenant}
   AND  TGT_PJT_CD = #{p.pjtCd}

mapper.delete:
DELETE FROM PCM_TGT_PJT_MSTR
 WHERE  TEN_ID     = #{g.tenant}
   AND  TGT_PJT_CD = #{p.pjtCd}
